var loopback = require('loopback');
module.exports = function(RED) {

    function CustomMethodCallbackNode(config) {
        RED.nodes.createNode(this, config);
        var node = this;
        node.on('input', function(msg) {
            if (msg.customMethodCallback) {
                msg.customMethodCallback(null, msg.payload);
            }
        });
        node.on('close', function() {
        });
    }

    RED.nodes.registerType("custom-method-callback", CustomMethodCallbackNode);
}
